DROP TABLE IF EXISTS `wanfang`;CREATE TABLE `wanfang` (  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '主键自增长',  `title` varchar(200) NOT NULL COMMENT '标题',  `abstract` text NOT NULL COMMENT '摘要',  `author` text NOT NULL COMMENT '作者(中文+英文可能有)',  `unit` text NOT NULL COMMENT '工作单位',  `magezine` varchar(200) NOT NULL COMMENT '发表的期刊',  `file_url` varchar(200) NOT NULL COMMENT '论文url',  `time` varchar(50) NOT NULL COMMENT '发表时间',  `keyword` text NOT NULL COMMENT '关键字',  `url` varchar(100) NOT NULL COMMENT '页面url',  `spider_time` varchar(50) NOT NULL COMMENT '爬取时间',  `paper_flag` int(1) NOT NULL DEFAULT '0' COMMENT '论文pdf是否爬取',  `file_path` varchar(100) DEFAULT NULL,  `transform_flag` int(1) NOT NULL DEFAULT '0',  `transform_text_path` varchar(100) DEFAULT NULL,  `pushed` int(1) DEFAULT '0',  `type_id` int(5) DEFAULT '0',  `indexed` int(1) DEFAULT '0',  PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;alter table wanfang add context text DEFAULT null;ALTER TABLE wanfang MODIFY COLUMN title text;